import React, { Component } from 'react';
import styles from './Board.module.css';
import MemberTile from '../../components/Board/MemberTile';
import MemberDetail from '../../components/Board/MemberDetail';
import NewMember from '../../components/Board/NewMember';
import axios from 'axios';

class Board extends Component {

	state = {
		members: []
	}

	componentDidMount() {
		axios.get('http://www.kazoopromotions.com/api/boardmembers')
			.then(resp => {
				//console.log(resp);
				this.setState({members: resp.data});
			});
	}

	render() {
		const members = this.state.members.map(member => {
			return <MemberTile firstName={member.FirstName} lastName={member.LastName} title={member.Title} />
		});

		return (
			<div>
				<section className={styles.memberList}>
					<MemberTile />
					<MemberTile />
					<MemberTile />
				</section>
				<section>
					<MemberDetail />
				</section>
				<section>
					<NewMember />
				</section>
			</div>
		);
	}
}

export default Board;
