import React from 'react';
import styles from './Input.module.css';

const input = (props) => {
	let elem = null;

	const classes = [];
	let hasInvalidClass
	if (props.attrs.className) classes.push(props.attrs.className);
	if (!props.valid && props.attrs.className.indexOf('invalid') < 0) classes.push('invalid');
	props.attrs.className = classes.join(' ');

	switch (props.kind) {
		case ('textarea'):
			elem = <textarea {...props.attrs} onChange={props.changed} />
			break;
		default:
			elem = <input {...props.attrs} onChange={props.changed} />
	}

	return (
		<div className={styles.input}>
			<label>{props.label}</label>
			{elem}
		</div>
	);
};

export default input;
