import React from 'react';
import styles from './Input.module.css';

const input = (props) => {
	let elem = null;
	let placeholder = 'enter ' + props.label.toLowerCase();
	let attrs = { placeholder, ...props };
	switch (props.kind) {
		case ('textarea'):
			elem = <textarea {...attrs} />
			break;
		default:
			elem = <input {...props} />
	}

	return (
		<div className={styles.input}>
			<label>{props.label}</label>
			{elem}
		</div>
	);
};

export default input;
