import React, { Component } from 'react';
import styles from './App.module.css';
import Products from '../components/Products/Products';

class App extends Component {

	state = {
		showProducts: false,
		products: [{
		    id: 1,
		    name: 'Pocket Knife',
		    price: 12.98,
		    unitQuantity: 1,
		    inscription: '',
		    quantity: 0
		  },
		  {
		    id: 2,
		    name: 'Golf Balls',
		    price: 22.50,
		    unitQuantity: 5,
		    inscription: '',
		    quantity: 0
		  },
		  {
		    id: 3,
		    name: 'Wine Glass',
		    price: 11.49,
		    unitQuantity: 2,
		    inscription: '',
		    quantity: 0,
		    description: 'Beautiful crystal wine glasses'
		  }
		]
	}

	toggleProductsHandler = () => {
		this.setState({ showProducts: !this.state.showProducts });
	};

  render() {
		let buttonClass = styles.off;

		if (this.state.showProducts) {
			buttonClass = styles.on;
		}

    return (<>
			<div className={styles.App} key="item1">
				<h1>React Fundamentals</h1>
				<button className={buttonClass} onClick={this.toggleProductsHandler}>Toggle Products</button>
				{this.state.showProducts ? <Products></Products> : null}
      </div>
		</>)
  }
}

export default App;
