import React, { Component } from 'react';
import Product from './Product';

class Products extends Component {

		render() {
console.log('(Products.js) - in component function');
return ( <
    div > {
      props.products.map((product, idx) =>
        ( < Product key = {
            product.id
          }
          name = {
            product.name
          }
          price = {
            product.price
          }
          quantity = {
            product.quantity
          }
          unitQuantity = {
            product.unitQuantity
          }
          inscription = {
            product.inscription
          }
          removeClicked = {
            () => props.productRemoved(idx)
          }
          inscriptionChanged = {
            (evt) => props.inscriptionChanged(evt, product.id)
          }
          buyClicked = {
            () => props.buyClicked(product.id)
          } >
          {
            product.description
          } < /Product>
        ))
    } <
    /div>);
		}
		
	}


export default products;
