import React from 'react';
import Input from './components/Forms/Input';

const formBuilder = {
	configInput(kind, type, label, placeholder, value='') {
		const settings = {
			kind,
			label,
			value,
			config: {}
		}
		
		if (typeof type !== 'undefined') {
			settings.config.type = type;
		}
		if (typeof placeholder !== 'undefined') {
			settings.config.placeholder = placeholder;
		} else {
			settings.config.placeholder = 'enter ' + label.toLowerCase();
		}

		return settings;
	},

	buildForm(settings) {
		const elements = [];
		for (let key in settings) {
			elements.push({
				id: key,
				config: settings[key]
			});
		}

		const content = elements.map(elem => (
			<Input  />
		));

		return content;
	}

};

export default formBuilder;
