import React, { Component } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import styles from './FullPost.module.css';

class FullPost extends Component {
	state = {
		loadedPost: null
	}

	componentDidUpdate() {
		if (this.props.id && (!this.state.loadedPost || this.state.loadedPost.id !== this.props.id)) {
			axios.get('http://jsonplaceholder.typicode.com/posts/' + this.props.id)
				.then(resp => {
					this.setState({loadedPost: resp.data});
				})
				.catch(err => {
				  toast.error(err.toString());
				});
		}
	}

	deletePostHandler = () => {
		axios.delete('http://jsonplaceholder.typicode.com/posts/' + this.props.id)
			.then(resp => {
				console.log(resp);
			});
	}

	render () {
		let post = <p className={styles.instructions}>Please select a Post!</p>;
		if (this.props.id) {
			post = <p className={styles.instructions}>Loading the Post!</p>
		}
		if (this.state.loadedPost) {
			post = (
				<div className={styles.fullPost}>
					<h1>{this.state.loadedPost.title}</h1>
					<p>{this.state.loadedPost.body}</p>
					<div className={styles.edit}>
						<button className="Delete" onClick={this.deletePostHandler}>Delete</button>
					</div>
				</div>
			);
		}
		return post;
	}
}

export default FullPost;
