import React, { Component } from "react";
import styles from "./MemberDetail.module.css";
import axios from "axios";

class MemberDetail extends Component {
  state = {
    loadedMember: null
  };

  componentDidUpdate() {
    if (this.props.id && (!this.state.loadedMember || this.state.loadedMember.Id !== this.props.id)) {
      axios
        .get("http://www.kazoopromotions.com/api/boardmembers/" + this.props.id)
        .then(resp => {
          this.setState({ loadedMember: resp.data });
        });
    }
	}
	
	deleteMemberHandler = () => {
		axios.delete('http://www.kazoopromotions.com/api/boardmembers/')
	};

  render() {
    let content = <p className={styles.instructions}>Please select a Post!</p>;
    if (this.props.id) {
      content = <p className={styles.instructions}>Loading the Board Member!</p>;
    }
    if (this.state.loadedMember) {
      content = (
        <div className={styles.memberDetail}>
          <h1>
            {this.state.loadedMember.FirstName}{" "}
            {this.state.loadedMember.LastName}
          </h1>
          <p className={styles.jobTitle}>{this.state.loadedMember.Title}</p>
					<div dangerouslySetInnerHTML={{__html: this.state.loadedMember.Bio}}></div>
          <div className={styles.edit}>
            <button className="Delete">Delete</button>
          </div>
        </div>
      );
    }
    return content;
  }
}

export default MemberDetail;
