import React from 'react';

const formBuilder = {
	configInput(kind, type, label, placeholder, value='') {
		const settings = {
			kind,
			label,
			value,
			config: {}
		}
		
		if (typeof type !== 'undefined') {
			settings.config.type = type;
		}
		if (typeof placeholder !== 'undefined') {
			settings.config.placeholder = placeholder;
		} else {
			settings.config.placeholder = 'enter ' + label.toLowerCase();
		}

		return settings;
	},

	buildForm(settings) {
		
	}

};

export default formBuilder;
